package algorithms.tabu.solution;

public class Writer {

	public Writer() {
		// TODO Auto-generated constructor stub
	}
	

	public static void printSolution(int[] solution) 
	{
	   for (int i = 0; i < solution.length; i++) 
	   {
	       System.out.print(solution[i] + " ");
	   }
	   System.out.println();
	}

}
